<?php if (is_admin()):    require_once 'admin/index.php';endif;add_filter('pfm_roles', 'prm_setup_roles', 10);function prm_setup_roles($roles){        $prmRoles = get_option('rcl_fields_prime-role-manager');        if(!$prmRoles) return $roles;        $roles = array();    foreach($prmRoles as $prmRole){        $caps = array();                if(isset($prmRole['capabilities'])){            foreach($prmRole['capabilities'] as $capID){                $caps[$capID] = 1;            }        }                $roles[$prmRole['slug']] = array(            'name' => $prmRole['title'],            'capabilities' => $caps        );    }        return $roles;}